$(document).ready(function () {
	$(".ex-menu-3 div").each(function () {
		var $ul = $(this).find("ul");
		$(this).hover(function (e) {
			var tempHeight = $ul.css("height", "auto").height();
			$ul
				.height(0)
				.stop()
				.animate({ height: tempHeight.toString() }, 500);
		}, function () {
			$ul
				.stop()
				.animate({ height: "0" }, 500);
		});
	});
});

$(".ex-menu-4 div").each(function () {
	var 
		$ul = $(this).find("ul"),
		tempTimer; 

	$(this).hover(function () {
		var tempHeight = $ul.css("height", "auto").height();
		$ul.height(0);
		tempTimer = setTimeout(function () {
			$ul
				.stop()
				.animate({ height: tempHeight.toString() }, 500);
		}, 1000);
	}, function () {
		clearTimeout(tempTimer);
		$ul
			.stop()
			.animate({ height: "0" }, 500);
	});
});

$(".ex-menu-5 div").mousemove(function (e) {
	$.data(this, "x", e.pageX);
	$.data(this, "y", e.pageY);
}).data("x", 0).data("y", 0)
.each(function () {
	var
		$this = $(this),
		$ul = $this.find("ul"),
		tempTimer, tempX, tempY;

	$this.hover(function () {
		var tempHeight = $ul.css("height", "auto").height();
		$ul.height(0);
		tempTimer = setInterval(function () {
			if (tempX == $this.data("x") && tempY == $this.data("y")) {
				clearInterval(tempTimer);
				$ul
					.stop()
					.animate({ height: tempHeight.toString() }, 500);
			}
			tempX = $this.data("x");
			tempY = $this.data("y");
		}, 50);
	}, function () {
		clearInterval(tempTimer);
		$ul
			.stop()
			.animate({ height: "0" }, 500);
	});
});

