@media (min-width: @screen-sm-min) {
	.call-to-action:nth-child(n+3) {
		display: none;
	}
}
@media (min-width: @screen-md-min) {
	.call-to-action:nth-child(n+3) {
		display: block;
	}
}
@media (min-width: @screen-lg-min) {
	.call-to-action:nth-child(n+3) {
		display: none;
	}
}
.secondary-nav {
	.make-sm-column(12);
}
.utils {
	.make-sm-column(3);
	.make-sm-column-push(9);
}
.legals {
	.make-sm-column(9);
	.make-sm-column-pull(3);
}
