	.make-sm-column(4);
}
.legals,
.brand {
  .text-left;
}
.utils {
  .text-center;
}
@media (min-width: @screen-sm-min) {
  .utils,
  .brand {
    .text-right;
  }
  .legals {
    .text-left
  }
}
