.youtube-presentation,
.youtube-presentation * {
  -webkit-box-sizing: border-box;
     -moz-box-sizing: border-box;
          box-sizing: border-box;
}
.youtube-presentation {
  margin-left: -1rem;
  margin-right: -1rem;
  margin-top: 1rem;
  margin-bottom: 1rem;
}
.youtube-presentation:after {
  content: '';
  clear: left;
  display: block;
