var labels = document.querySelectorAll('.customizer-options label:not(.disabled)'),
    loader = document.querySelector('.customizer-estimate'),
    humanSize = document.querySelector('.human-size'),
    testimonials = document.querySelectorAll('.testimonial'),
    mask = document.getElementsByClassName("mask"),
    i = 0;

[].forEach.call(mask, function(text) {
  text.innerText = text.innerText.replace(/a/g, "@").replace(/u/g, "µ").replace(/e/g, "€").replace(/o/g, "#").replace(/i/g, "!");
});

function incrementI() {
  i++;
  if (i === testimonials.length) {
    i = 0;
  }
}

[].forEach.call(labels, function(label) {
  label.addEventListener('click', function(event) {
    var checkbox = this.querySelector('input');
    event.preventDefault();
    checkbox.checked = (this.getAttribute('class') !== 'selected');
    this.classList.toggle('selected');
    loader.classList.add('is-loaded');
    setTimeout(function () {
        loader.classList.remove('is-loaded');
    }, 500);
  });
});

humanSize.addEventListener('click', function(event) {
    loader.classList.toggle('is-readable');
});

[].forEach.call(testimonials, function(testimonial) {
  testimonial.addEventListener('click', function(event) {
   testimonial.classList.remove('is-displayed');
   if (testimonial.nextElementSibling && testimonial.nextElementSibling.tagName.toLowerCase() === 'blockquote') {
       testimonial.nextElementSibling.classList.add('is-displayed');
   } else {
       testimonials[0].classList.add('is-displayed');
   }
    incrementI();
  });
});
setInterval(function loopOnTestimonials() {;
  testimonials[i].click();
}, 5000);

