            openTreasures: function () {
                console.log("Open Treasures");
                var temp,
                    first = treasuresStackNext.shift(),
                    card = document.querySelector(".stack-treasures .discard"),
                    title = card.getElementsByClassName("title")[0],
                    extension = card.getElementsByClassName("extension")[0],
