                if (!first) {
                    treasuresStackNext = treasuresStackDiscard;
                    treasuresStackDiscard = [];
                    if (this.munchkinomicon) { temp = treasuresStackNext.pop(); }
                    treasuresStackNext.shuffle();
                    if (this.munchkinomicon) { treasuresStackNext.unshift(temp); }
                    this.treasuresLastDiscard = false;
                    return false;
                }

                if (type === "munchkinomicon") {
                    treasuresStackDiscard.push(first);
                } else {
                    treasuresStackDiscard.unshift(first);
