                    donjonsStackNext.shuffle();
                    this.donjonsLastDiscard = false;
                    return false;
                }

                donjonsStackDiscard.unshift(first);
                this.donjonsLastDiscard = true;

                title.innerHTML = first.title;
                title.setAttribute("class", "title " + first.type);
                extension.innerHTML = first.extension.replace("munchkin-", "").replace("munchkin", "1").toUpperCase().replace("7-O", "7o");
                progress.innerHTML = donjonsStackDiscard.length + "/" + parseInt(donjonsStackDiscard.length + donjonsStackNext.length);
                type.innerHTML = first.type
                    .replace("fields", "Donjon");

                console.log(donjonsStackNext);
                console.log(donjonsStackDiscard);
            },
            openBooks: function () {
                console.log("Open Books");
                var first = booksStackNext.shift(),
                    card = document.querySelector(".stack-books .discard"),
                    title = card.getElementsByClassName("title")[0],
                    extension = card.getElementsByClassName("extension")[0],
                    type = card.getElementsByClassName("type")[0],
                    progress = card.getElementsByClassName("progress")[0];

                if (!first) {
                    booksStackNext = booksStackDiscard;
                    booksStackDiscard = [];
                    booksStackNext.shuffle();
                    this.booksLastDiscard = false;
                    return false;
                }

                booksStackDiscard.unshift(first);
                this.booksLastDiscard = true;

                title.innerHTML = first.title;
                title.setAttribute("class", "title " + first.type);
                extension.innerHTML = first.extension.replace("munchkin-", "").replace("munchkin", "1").toUpperCase().replace("7-O", "7o");
                progress.innerHTML = booksStackDiscard.length + "/" + parseInt(booksStackDiscard.length + booksStackNext.length);
                type.innerHTML = first.type
                    .replace("spells", "Sorts");

                console.log(booksStackNext);
                console.log(booksStackDiscard);
            },
            stopShuffle: function () {
                this.doorsLastDiscard = false;
                this.donjonsLastDiscard = false;
                this.treasuresLastDiscard = false;
                this.booksLastDiscard = false;
                this.munchkinomicon = false;
                doorsStackNext = [];
                doorsStackDiscard = [];
                donjonsStackNext = [];
                donjonsStackDiscard = [];
                booksStackNext = [];
                booksStackDiscard = [];
                treasuresStackNext = [];
                treasuresStackDiscard = [];
                shuffle.classList.remove("running");
            }
        }
    });
});

