    function closePopup($popup) {
      /* ...permettre au clique sur le background qu'elle se ferme... */
      $popup.click(function (e) {
        e.preventDefault();
        $popup.removeClass("opened")

      /* ...mais pas que le contenu utile la ferme... */
      }).find(".content").click(function (e) {
        e.stopPropagation();

      /* ...mais que le bouton Close la ferme. */
      }).find(".close").click(function (e) {
        e.preventDefault();
        $popup.removeClass("opened")
      });
    }

    /* Chaque élément d'ouverture $sender... */
    function popupVideo() {
      $senders.each(function (i) {
        var $sender = $(this),
            $target = $targets.eq(i);

        /* ...cible sa popup associée $target */
        $sender.click(function (e) {
          /* ...l'ouvre au clique... */
          e.preventDefault();
          $target.addClass("opened");
        });

        /* ...et met en place sa fermeture. */
        closePopup($target)
      });
    }

    /* 
     * Application de la fonction à la 
     * lecture de celle-ci...
     */
    popupVideo();

    /* 
     * ...puis à chaque redimentionnement de 
     * page.
     */
    $(window).resize(function () {
      popupVideo(); 
    });
  };

}(website));

/* Application de nos deux fonctions sur le 
 * HTML préparé en amont.
 */
website.sameHeight($(".youtube-presentation.ex1 .presentation, .youtube-presentation.ex1 .video"));
website.popupVideo($(".youtube-presentation.ex1 .video a"), $(".youtube-presentation.ex1 .popup"));
website.sameHeight2(
  $(".youtube-presentation2 .presentation, .youtube-presentation2 .video"),
  "(min-width: 480px) AND (max-width: 779px), (min-width: 992px)"
);
website.popupVideo2(
  $(".youtube-presentation2 .video a"), $(".youtube-presentation2 .popup"),
  "(min-width: 480px) AND (min-height: 320px)"
);

