function angularProcess() {
    var example2 = angular.module("example2", []),
        example3 = angular.module("example3", []),
        example4 = angular.module("example4", []),
        example5 = angular.module("example5", []),
        template = document.getElementsByClassName("popular-pokemon-template")[0],
        content = document.importNode(template.content, true),
        list = document.getElementsByClassName("popular-pokemon-list")[0],
        pokemons = list.getElementsByTagName("li"),
        popularPokemons = [];

    Array.prototype.forEach.call(pokemons, function (pokemon) {
        popularPokemons.push({ 
            name: pokemon.getElementsByClassName("pokemon")[0].textContent, 
            cover: (pokemon.getElementsByClassName("cover").length > 0) ? true : false
        });
    });

    list.appendChild(content);

    example2.controller("controller2", function($scope) {
        $scope.name = "John Doe";
    });

    example5.config(function($locationProvider) {
        //$locationProvider.html5Mode(true);
    }).controller("controller5", function($scope, $location, $sce) {
        var content = {
            "/": "<h2 style=\"margin-top: 0\">Bienvenue !</h2><p>Utilisez les boutons pour naviguer.</p>",
            "/products/": "<h2 style=\"margin-top: 0\">Notre Wallpaper<h2><p style=\"margin-top: 0\"><img style=\"max-width: 100%;margin-top: 0\" src=\"http://blog.haeresis.fr/upload/image/the_moment_by_dzsurnik.jpg\" alt=\"The Moment\"></p>",
            "/contact/": "<h2 style=\"margin-top: 0\">Contactez-nous</h2><p><input type=\"text\"></p>",
        };
        $scope.$on("$locationChangeStart", function (event, next) {
            var hash = /\/#(\/[a-z]*\/?)/g.exec(next);
            $scope.content = $sce.trustAsHtml(content[hash ? hash[1] : "/"]);
        });
        $scope.goTo = function (url) {
            $location.path(url).replace();
        }
    }).directive("mainModule", function() {
        return {
            restrict : "A",
            template : "<div data-ng-bind-html='content'></div>" + 
                       "<button data-ng-click=\"goTo('/')\">Accueil</button>" + 
                       "<button data-ng-click=\"goTo('/products/')\">Produits</button>" + 
                       "<button data-ng-click=\"goTo('/contact/')\">Contactez-nous</button>"
        };
    });

    example4.directive("newDirective", function() {
        return {
            restrict : "A",
            template : "<p>Ceci a été créer par une Directive !</p>"
        };
    });

    example3.controller("controller3", function($scope) {
        $scope.popularPokemons = [
            { name: "Pikachu", cover: true }, 
            { name: "Bulbizarre", cover: false }, 
            { name: "Carapuce", cover: false }, 
            { name: "Dracaufeu", cover: true }, 
            { name: "Mewtwo", cover: false }, 
        ];
        $scope.add = function () {
            $scope.addPokemon.cover = ($scope.addPokemon.cover === "true");
            $scope.popularPokemons.push($scope.addPokemon);
            $scope.addPokemon = { cover: "false" };
        }
        $scope.remove = function (pos) {
            $scope.popularPokemons.splice(pos, 1);
        }
    });

    angular.bootstrap(document.getElementById("app2"), ['example2']);
    angular.bootstrap(document.getElementById("app3"), ['example3']);
    angular.bootstrap(document.getElementById("app4"), ['example4']);
    angular.bootstrap(document.getElementById("app5"), ['example5']);
}

(function () {
    var scr = document.createElement('script');
    scr.src = "https://cdnjs.cloudflare.com/ajax/libs/angular.js/1.5.0/angular.js";
    document.body.appendChild(scr);

    scr.addEventListener("load", function () {
        angularProcess();
    });
 }());

