Array.prototype.shuffle = function (n) {
    function randomInt(mini, maxi) {
        var nb = mini + (maxi + 1 - mini) * Math.random();
        return Math.floor(nb);
    }

    if (!n) {
        n = this.length;
    }

    if (n > 1) {
        var i = randomInt(0, n - 1),
            tmp = this[i];

        this[i] = this[n - 1];
        this[n - 1] = tmp;
        this.shuffle(n - 1);
    }
};

function munchkinList(selector) {
    var doors = document.querySelectorAll(selector + " .doors li"),
        treasures = document.querySelectorAll(selector + " .treasures li"),
        donjons = document.querySelectorAll(selector + " .donjons li"),
        seals = document.querySelectorAll(selector + " .seals li"),
        spells = document.querySelectorAll(selector + " .books li"),
        doorsList = [],
        treasuresList = [],
        donjonsList = [],
        sealsList = [],
        spellsList = [];

    [].forEach.call(doors, function(item) {
        doorsList.push({
            name: item.textContent,
            class: item.getAttribute("class")
        });
        item.parentNode.removeChild(item);
    });

    [].forEach.call(treasures, function(item) {
        treasuresList.push({
            name: item.textContent,
            class: item.getAttribute("class")
        });
        item.parentNode.removeChild(item);
    });

    [].forEach.call(donjons, function(item) {
        donjonsList.push({
            name: item.textContent,
            class: item.getAttribute("class")
        });
        item.parentNode.removeChild(item);
    });

    [].forEach.call(seals, function(item) {
        sealsList.push({
            name: item.textContent,
            class: item.getAttribute("class")
        });
        item.parentNode.removeChild(item);
    });

    [].forEach.call(spells, function(item) {
        spellsList.push({
            name: item.textContent,
            class: item.getAttribute("class")
        });
        item.parentNode.removeChild(item);
    });

    return {
        doorsList: doorsList,
        treasuresList: treasuresList,
        donjonsList: donjonsList,
        sealsList: sealsList,
        spellsList: spellsList
    };
}

