            stopShuffle: function () {
                var self = this,
                    shuffle = self.$el;

                this.doorsLastDiscard = false;
                this.donjonsLastDiscard = false;
                this.sealsLastDiscard = false;
                this.treasuresLastDiscard = false;
                this.booksLastDiscard = false;
                this.munchkinomicon = false;
                doorsStackNext = [];
                doorsStackDiscard = [];
                donjonsStackNext = [];
                donjonsStackDiscard = [];
                sealsStackNext = [];
                sealsStackDiscard = [];
                booksStackNext = [];
                booksStackDiscard = [];
                treasuresStackNext = [];
                treasuresStackDiscard = [];
                shuffle.classList.remove("running");
            }
        }
    });
});

document.getElementsByTagName("body")[0].appendChild(script);

